import React, { useState } from "react";
import Square from "./squares/Square";
import classes from "./GameBoard.module.css";

const GameBoard = ({ squares, clickOnBoard }) => {
  const [boardValue, setBoardValue] = useState([Array(9).fill(null)]);
  const [xIsNext, setXisNext] = useState(false);

  const clickSquareHandler = (index) => {
    setXOrO(!xIsNext);
    const newBoardValue = [...boardValue];
    xIsNext ? (newBoardValue[index] = "X") : (newBoardValue[index] = "O");
    setBoardValue(newBoardValue);
    console.log(boardValue);
  };

  const renderSquare = (index) => {
    return (
      <Square
        squareValue={boardValue[index]}
        clickSquare={() => clickSquareHandler(index)}
      />
    );
  };

  return (
    <div className={classes.GameBoard}>
      {renderSquare(0)}
      {renderSquare(1)}
      {renderSquare(2)}
      {renderSquare(3)}
      {renderSquare(4)}
      {renderSquare(5)}
      {renderSquare(6)}
      {renderSquare(7)}
      {renderSquare(8)}
    </div>
  );
};

export default GameBoard;
