import React, { useState } from "react";
import Square from "./squares/Square";
import classes from "./GameBoard.module.css";

const GameBoard = ({ squares, clickOnBoard }) => {
  const [boardValue, setBoardValue] = useState([Array(9).fill(null)]);
  const [xOrO, setXOrO] = useState(true);
  const clickSquareHandler = () => {
    setXOrO(!xOrO);
    const newSquareValues = [...squareValues];
    xOrO ? newSquareValues.push("X") : newSquareValues.push("O");
    setSquareValues(newSquareValues);
    console.log(squareValues);
  };

  return (
    <div className={classes.GameBoard}>
      <Square squareValue={squareValue} clickSquare={clickSquareHandler} />
    </div>
  );
};

export default GameBoard;
