import React, { useState } from "react";
import Square from "./squares/Square";
import classes from "./GameBoard.module.css";
import Button from "../Button/Button";

const GameBoard = () => {
  const [boardValue, setBoardValue] = useState([Array(9).fill(null)]);
  const [xIsNext, setXisNext] = useState(false);

  const clickSquareHandler = (index) => {
    setXisNext(!xIsNext); //determine X or O
    const newBoardValue = [...boardValue]; //avoiding mutate the state directly
    xIsNext ? (newBoardValue[index] = "X") : (newBoardValue[index] = "O");
    setBoardValue(newBoardValue);
  };

  //each square is different, render with different index
  const renderSquare = (index) => {
    return (
      <Square
        squareValue={boardValue[index]}
        clickSquare={() => clickSquareHandler(index)}
      />
    );
  };
  //set board back to empty
  const restartGameHandler = () => {
    setBoardValue([Array(9).fill(null)]);
  };

  //calculate the winner

  const winnerCalculator = (squares) => {
    const winningLines = [
      [0, 1, 2],
      [3, 4, 5],
      [6, 7, 8],
      [0, 3, 6],
      [2, 5, 8],
      [2, 4, 6],
    ];
    for (let i = 0; i < winningLines.length; i++) {
      const [a, b, c] = winningLines[i];

      if (
        squares[a] &&
        squares[a] === squares[b] &&
        squares[b] === squares[c]
      ) {
        return squares[a];
      }
      return null;
    }
  };
  let GameStatus;
  const winner = winnerCalculator(boardValue);
  GameStatus = winner ? `${winner}` : xIsNext ? "X" : "O";

  return (
    <div className={classes.GameContainer}>
      <Button clickButton={restartGameHandler} buttonName="Start" />
      <p className={classes.text}>Next is {GameStatus} </p>
      <p className={classes.text}>The winner is {GameStatus} </p>
      <div className={classes.GameBoard}>
        <div className={classes.row}>
          {renderSquare(0)}
          {renderSquare(1)}
          {renderSquare(2)}
        </div>
        <div className={classes.row}>
          {renderSquare(3)}
          {renderSquare(4)}
          {renderSquare(5)}
        </div>
        <div className={classes.row}>
          {renderSquare(6)}
          {renderSquare(7)}
          {renderSquare(8)}
        </div>
      </div>
    </div>
  );
};

export default GameBoard;
